if (!first) &#123;
first &#x3D; new Date().getTime();
console.log(‘再按一次退出应用’);
setTimeout(function() &#123;
first &#x3D; null;
&#125;, 1000);
&#125; else &#123;
if (new Date().getTime() - first &lt; 1500) &#123;
plus.runtime.quit();
&#125;
&#125;
&#125;, false);
&#125;
&#125;)
&#125;);

